/* ==UserStyle==
@name           Bamboo-X
@namespace      https://github.com/Catgrills/Bamboo-X
@version        1.0.0
@description    An userstyle for Exhentai called Bamboo-X.
@author         Catgrills/Katgrills
@preprocessor uso
@var select theme 'Theme' {
	"Light": `
	:root {
        --body-bg: #F0F1F5 !important;
		--card-bg: #FFF !important;
		--card-bg-2: #FAFAFA !important;
		--card-bg-3: #E6DCDB !important;
		--card-bg-4: #D4DBC8 !important;
		--header-bg: #EBBD63 !important;
		--border-color: #E0E0E0 !important;
		--border-color-2: #E6567A !important;
		--primary-color: #212121 !important;
		--secondary-color: #999 !important;
		--invert: invert(0) !important;
	}
	`,
	"Dark":`
	:root {
	    --body-bg: #383745 !important; 
		--card-bg: #22202B !important;
		--card-bg-2: #383745 !important;
		--card-bg-3: #7D6962 !important;
		--card-bg-4: #CA8D6E !important;
		--header-bg: #22202B !important;
		--border-color: #111 !important;
		--border-color-2: #CA8D6E !important;
		--primary-color: #FFF !important;
		--secondary-color: #DDD !important;
		--invert: invert(1) !important;
	}
	`
}
==/UserStyle== */
@-moz-document domain("exhentai.org") {
/*[[theme]]*/
/* GENERAL */

	/** DESCRIPTION : set main font and background, plus change/remove elements from all page **/
	* {
		font-family: "Roboto", "ぼくたちのゴシック２ボールド", "BM YEONSUNG" !important;
        text-shadow: none !important;
	}

	*::-moz-focus-inner {
		border: 0 !important;
	}

	html {
		overflow-x: hidden !important;
	}

	body {
		background: var(--body-bg) !important;
	}

	a:focus {
		outline: none !important;
	}

    select {
        -webkit-appearance: none;
        -moz-appearance: none;
        text-indent: 0px;
        text-overflow: ellipsis;
        white-space: nowrap;
    }

	input[type="button"]:enabled:hover,
	input[type="submit"]:enabled:hover,
	input[type="button"]:enabled:focus,
	input[type="submit"]:enabled:focus {
        background: var(--card-bg-2) !important;
		border-color: var(--border-color) !important;
		outline: none !important;
	}

	.ido {

		/*** MAIN CONTENT ***/
		background: transparent !important;
		border: none !important;
		padding: 10px !important;
		margin-top: 140px !important;
	}

	.c1 .c8 {
		display: none !important;
	}

	input[type="checkbox"] {
		position: relative;
		top: 0px;
	}

	/* GALLERY LIST HELPER 
    #tagButtonsDiv {
        background: #22202B !important;
        border: 1px solid #000 !important;
        padding: 40px 0px !important;
        margin-bottom: 8px !important;
        margin-left: 5px !important;
        margin-top: -24px !important;
        margin-right: 4px !important;
    }
    #tagButtonsDiv > div {
        padding: 10px !important;
        border: 1px solid #000 !important;
    }
    #tagButtonsDiv > div * {
        font-size: 10pt !important;
    }
    table.itg {
        margin-left: 5px !important;
        margin-right: -10px !important;
    }
    table.itg .it3 {
        margin-left: 0px !important;
        margin-top: 10px !important;
    }
    table.itg .it4 {
        margin-top: 10px !important;
    }
	*/

	/* HEADER */

	/** DESCRIPTION : change the elements of the header **/
	p#nb {

		/*** HEADER ***/
		background: var(--header-bg) !important;
		border-bottom: 3px solid var(--border-color-2) !important;
		padding: 32px 0px 30px 130px !important;
		text-align: left !important;
		position: absolute !important;
		top: -2px !important;
		right: 0px !important;
		left: 0px !important;
		margin-left: -2px !important;
		z-index: 999 !important;
	}

	p#nb a {

		/*** HEADER LINKS ***/
		color: #F8F8F8 !important;
		font-family: "Roboto" !important;
		font-size: 15pt !important;
		font-stretch: condensed !important;
	}

	p#nb a:hover {
		color: #EBEBEB !important;
	}

	p#nb img {

		/*** HEADER ICONS ***/
		visibility: hidden !important;
	}

	/* HOME */

	/** DESCRIPION : change the elements of the home page **/
	#nb a:nth-of-type(1):before {

		/*** HOME SLOGAN ***/
		content: "exhentai.org" !important;
		color: #FFF !important;
		font-family: "Neuton" !important;
		font-size: 23pt !important;
		position: absolute !important;
		top: 19px !important;
		left: 30px !important;
		visibility: visible !important;
		z-index: 99 !important;
		transition: all .3s ease-in-out !important;
	}

	#nb a:nth-of-type(1):hover:before {
		color: #FACA9B !important;
	}

	#nb a:nth-of-type(1) {
		visibility: hidden;
	}

	#toppane .ih:before {
		color: #FFF !important;
		content: "へんたい" !important;
		font-family: "Honoka Antique-Kaku" !important;
		font-size: 40pt !important;
		text-shadow: 4px 3px 0px #EB9532, 9px 8px 0px rgba(255, 255, 255, .15) !important;
		position: absolute !important;
		margin-top: 72px !important;
		margin-left: 40px !important;
		visibility: visible !important;
	}

	#toppane .ih {
		visibility: hidden;
	}

	#searchbox {

		/*** HOME SEARCH BOX ***/
		background: no-repeat url("https://i.imgur.com/Ah4lMHY.jpg") center 34% !important;
		background-size: cover !important;
		border: 1px solid var(--border-color) !important;
		height: 120px !important;
		padding: 20px !important;
		width: auto !important;
		margin-right: -4px !important;
		margin-left: -4px !important;
		z-index: 10 !important;
	}

	form[action="https://exhentai.org/"] {
		background: var(--card-bg-2) !important;
		border: 1px solid var(--border-color) !important;
		height: 60% !important;
		width: 80% !important;
		position: fixed !important;
		top: 0px !important;
		right: 0px !important;
		bottom: 0px !important;
		left: 0px !important;
		margin: auto !important;
		overflow: hidden !important;
		z-index: 999 !important;
		visibility: hidden;
	}

	form[action="https://exhentai.org/"] .nopm input[name="f_search"] {
		background: var(--card-bg);
		border: 1px solid var(--border-color);
		border-radius: 1px !important;
		border-width: 0px 0px 1px 0px !important;
        color: var(--primary-color);
		font-size: 15pt !important;
		font-stretch: condensed !important;
		height: 30px !important;
		padding: 14px 16px 17px 16px !important;
		width: 100% !important;
		margin-left: 0px !important;
		margin-top: 0px !important;
	}

	input[value="Apply Filter"],
	input[value="Clear Filter"] {
		border: none !important;
		border-radius: 1px !important;
		height: 66px !important;
		width: 150px !important;
		line-height: 68px !important;
		top: -5px !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: bold;
		position: absolute !important;
		text-transform: uppercase !important;
	}

	input[value="Apply Filter"] {
		background: #344146;
		right: 146px !important;
	}

	input[value="Clear Filter"] {
		background: #B05F6D;
		right: -3px !important;
	}

	table.itc {

		/*** HOME SEARCH CATEGORIES ***/
		background: transparent !important;
		border-radius: 2px !important;
		padding: 8px 0px !important;
		height: auto !important;
		width: auto !important;
		position: absolute;
		top: 0px !important;
		right: 0px !important;
		bottom: 0px !important;
		left: -12px !important;
		margin: 100px auto auto auto !important;
	}

	.itc tr {
		display: inline-block !important;
	}

	.itc .ic {

		/*** HOME SEARCH CATEGORIES ICONS ***/
		border: 1px solid var(--border-color) !important;
		border-radius: 1px !important;
		height: 0px !important;
		width: 0px !important;
		padding: 18vh 0px 0px 14vw !important;
		margin-bottom: 6px;
	}

    :root {
     	--doujinshi : url("https://i.imgur.com/PZ06V9H.jpg") !important;
        --manga : url("https://i.imgur.com/4PQwSBP.jpg") !important;
        --artistcg: url("https://i.imgur.com/OGV8S1z.jpg") !important;
        --gamecg: url("https://i.imgur.com/hZBx6F3.jpg") !important;
        --western: url("https://i.imgur.com/D9OdyqB.jpg") !important;
        --nonh: url("https://i.imgur.com/lIsnRWe.jpg") !important;
        --imageset: url("https://i.imgur.com/0WRwUHm.jpg") !important;
        --cosplay: url("https://i.imgur.com/Adqz0Hr.jpg") !important;
        --asian: url("https://i.imgur.com/bL2WqFy.jpg") !important;
        --misc: url("https://i.imgur.com/qdxrE1t.jpg") !important;
    }

	.itdc img {
		border-radius: 50% !important;
		height: 0px !important;
		width: 0px !important;
		padding: 12px 0px 0px 12px !important;
	}

	.itc img[src*="_d"] {
		opacity: .3 !important;
	}

	#f_doujinshi_img {
		background: no-repeat var(--doujinshi) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/doujinshi"] img,
	form[name="favform"] img[src*="doujinshi"],
	.id4 .id41[title="Doujinshi"] {
		background: #EB6361 !important;
	}

	#f_manga_img {
		background: no-repeat var(--manga) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/manga"] img,
	form[name="favform"] img[src*="manga"],
	.id4 .id41[title="Manga"] {
		background: #EEE657 !important;
	}

	#f_artistcg_img {
		background: no-repeat var(--artistcg) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/artistcg"] img,
	form[name="favform"] img[src*="artistcg"],
	.id4 .id41[title="Artist CG Sets"] {
		background: #EBBD63 !important;
	}

	#f_gamecg_img {
		background: no-repeat var(--gamecg) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/gamecg"] img,
	form[name="favform"] img[src*="gamecg"],
	.id4 .id41[title="Game CG Sets"] {
		background: #5C9F97 !important;
	}

	#f_western_img {
		background: no-repeat var(--western) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/western"] img,
	form[name="favform"] img[src*="western"],
	.id4 .id41[title="Western"] {
		background: #F7E999 !important;
	}

	#f_imageset_img {
		background: no-repeat var(--imageset) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/imageset"] img,
	form[name="favform"] img[src*="imageset"],
	.id4 .id41[title="Image Sets"] {
		background: #47D2E9 !important;
	}

	#f_non-h_img {
		background: no-repeat var(--nonh) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/non-h"] img,
	form[name="favform"] img[src*="non-h"],
	.id4 .id41[title="Non-H"] {
		background: #ECF0F1 !important;
	}

	#f_cosplay_img {
		background: no-repeat var(--cosplay) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/cosplay"] img,
	form[name="favform"] img[src*="cosplay"],
	.id4 .id41[title="Cosplay"] {
		background: #AA8C91 !important;
	}

	#f_asianporn_img {
		background: no-repeat var(--asian) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/asianporn"] img,
	form[name="favform"] img[src*="asian"],
	.id4 .id41[title="Asian Porn"] {
		background: #FDE1F7 !important;
	}

	#f_misc_img {
		background: no-repeat var(--misc) center !important;
		background-size: cover !important;
	}

	a[href="https://exhentai.org/misc"] img,
	form[name="favform"] img[src*="misc"],
	.id4 .id41[title="Misc"] {
		background: #FFF0D6 !important;
	}

	#searchbox form .nopm a[onclick*="advsearch_pane"],
	#searchbox form .nopm a[onclick*="filesearch_pane"] {

		/*** HOME SEARCH OPTIONS ***/
		background: #383745 !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
		border: 1px solid #000 !important;
		border-radius: 1px !important;
		box-shadow: none !important;
		color: transparent;
		height: 0px !important;
		width: 0px !important;
		padding: 34px 0px 0px 34px !important;
		position: fixed !important;
		top: 25px !important;
		visibility: visible !important;
	}

	#trigger-box-btn {
		background: #383745 !important;
		background-image: url("http://www.pvhc.net/img226/gzypyldoqaxjhvtyyhei.png") !important;
		background-position: 92% center !important;
		background-repeat: no-repeat !important;
		background-size: 20px auto !important;
		border: 1px solid #000 !important;
		border-radius: 1px !important;
		color: #FFF !important;
		font-size: 12pt !important;
		font-stretch: condensed;
		font-weight: bold;
		height: 36px !important;
		width: 150px !important;
		padding: 0px 8px;
		text-align: left;
		text-transform: uppercase !important;
		position: fixed !important;
		top: 25px !important;
		right: 112px !important;
		z-index: 999 !important;
		visibility: visible !important;
	}

	#searchbox form .nopm a[onclick*="advsearch_pane"] {
		background-image: url("https://i.imgur.com/D6gWsU5.png") !important;
		background-size: 21px auto !important;
		right: 67px !important;
	}

	.itss {
		background: var(--card-bg) !important;
		border: 1px solid var(--border-color) !important;
		padding: 20px !important;
		height: 200px !important;
		width: 800px !important;
		position: fixed !important;
		top: 34vh !important;
		right: 0px !important;
		bottom: 0px !important;
		left: 0px !important;
		margin: auto !important;
		visibility: visible !important;
	}

	#searchbox form .nopm a[onclick*="filesearch_pane"] {
		background-image: url("https://i.imgur.com/oprVAfY.png") !important;
		background-size: 16px auto !important;
		right: 23px !important;
	}

	#fsdiv {
		background: var(--card-bg) !important;
		border: 1px solid var(--border-color) !important;
		padding: 20px 20px 50px 20px !important;
		height: 30%;
		width: 40% !important;
		position: fixed !important;
		top: -3vh !important;
		right: 0px !important;
		bottom: 20px !important;
		left: 0px !important;
		margin: auto !important;
		z-index: 999 !important;
		visibility: visible !important;
	}

	#fsdiv *,
	.itss * {
        color: var(--primary-color);
		font-family: "Roboto" !important;
		font-size: 14pt !important;
		font-stretch: condensed;
        font-weight: normal !important;
	}

    #adv42 {
		background: var(--card-bg-2) !important;
		border: 1px solid var(--border-color);
		border-radius: 1px !important;
		color: var(--secondary-color) !important;        
    }

	input[value="File Search"] {
		background: var(--card-bg-2) !important;
		border: 1px solid var(--border-color);
		border-radius: 1px !important;
		color: var(--secondary-color) !important;
		padding: 5px 10px !important;
		text-transform: uppercase !important;
		margin-top: 8px !important;
		margin-bottom: 8px !important;
	}

	input[name="sfile"] {
		background: var(--card-bg-2) !important;
		border: 1px solid var(--border-color) !important;
		padding: 2px 2px 3px 2px !important;
		margin-top: 1px !important;
	}

	.ip[style="margin-top:5px"] {

		/*** HOME RESULTS ***/
		background: var(--card-bg) !important;
		border: 1px solid var(--border-color) !important;
		border-radius: 2px !important;
		color: #FFF !important;
		font-size: 11pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		padding: 10px 20px !important;
		max-width: 150px !important;
		overflow: hidden !important;
		text-overflow: ellipsis !important;
		white-space: nowrap !important;
		text-decoration: none !important;
		text-transform: uppercase !important;
		position: fixed !important;
		bottom: 20px !important;
		right: 20px !important;
		visibility: hidden;
		z-index: 100 !important;
	}

	#iw {
		background: var(--card-bg) !important;
		border: 1px solid var(--border-color) !important;
		color: #D33257 !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
		padding: 10px 10px 18px 10px !important;
		width: 1174px !important;
		margin-top: -9px !important;
	}

    #iw * {
     	font-weight: normal !important;   
    }

	p[style="text-align:center; font-style:italic; margin-bottom:10px"] {
		border: 1px solid var(--border-color) !important;
        color: var(--secondary-color);
		padding: 20px !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
        font-style: normal !important;
		font-weight: normal !important;
		margin-top: -10px !important;
		margin-right: 2px !important;
		margin-left: 2px !important;
	}

	.ip[style="margin-top:5px"] span,
	.ip[style="margin-top:5px"] a[href*="random"] {
		display: none !important;
	}

	div#dmi,
	div[style="position:absolute; top:-18px; left:15px"] {

		/*** THUMBNAIL/LIST BUTTON ***/
		visibility: hidden;
	}

	#dmi a[href*="set=dm_t"],
	#dmi a[href*="set=dm_l"],
	div[style="position:absolute; top:-18px; left:15px"] a[href*="set=dm_t"],
	div[style="position:absolute; top:-18px; left:15px"] a[href*="set=dm_l"] {
		background: var(--card-bg) !important;
		border: 1px solid var(--border-color) !important;
		border-radius: 2px !important;
		color: var(--secondary-color) !important;
		font-size: 12pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		padding: 10px 20px !important;
		text-decoration: none !important;
		text-transform: uppercase !important;
		position: fixed !important;
		right: 20px !important;
		bottom: 25px !important;
		visibility: visible !important;
		z-index: 100 !important;
	}

    #dmi a[href*="set=dm_t"]:hover,
	#dmi a[href*="set=dm_l"]:hover,
	div[style="position:absolute; top:-18px; left:15px"] a[href*="set=dm_t"]:hover,
    div[style="position:absolute; top:-18px; left:15px"] a[href*="set=dm_l"]:hover {
        color: #F9AE74 !important;
    }

	div[style="position:relative; z-index:2"] {
		z-index: 99 !important;
	}

	div[style="position:relative; z-index:2"] > .ptt {

		/*** HOME PAGINATIONS ***/
		display: none !important;
	}

	.itg {

		/*** HOME THUMBNAIL CONTENT ***/
		background: var(--card-bg) !important;
		border: 1px solid var(--border-color) !important;
		max-width: 99% !important;
		margin-top: -9px !important;
		padding-top: 10px !important;
	}

	.itg .id1 {

		/*** THUMBNAILS ***/
		background: transparent !important;
		border: none !important;
		height: 335px !important;
	}

	.itg .id2 {
		max-height: 25px !important;
        height: auto !important;
		width: 200px !important;
		margin-bottom: 12px !important;
		text-align: left !important;
		transition: max-height 0.35s ease-out !important;
	}

	.id2 a {
        color: var(--secondary-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
        font-weight: normal !important;
	}

	.id1:not(.e-Highlighted):not(.e-Filtered) > .id2 {
		overflow: hidden !important;
	}

	.id1:not(.e-Highlighted):not(.e-Filtered):hover > .id2 > a {
		padding-bottom: 0px !important;        
	}

	.id1:hover > .id2 > a {
		background: transparent !important;
        transition: all 0.35s ease-in-out !important;
	}

	.id2:hover a {
		color: inherit !important;
		text-shadow: none !important;
	}

	.itg .id2:hover {
		max-height: 900px !important;
        transition: max-height 0.45s ease-in !important;
	}

	.itg .id3,
	.itg .id3 img {
		border-radius: 1px !important;
		z-index: 99 !important;
	}

	.itg .id3 {
		background: #17171A !important;
		border: 1px solid var(--border-color) !important;
		height: 280px !important;
	}

	.itg .id3 img {
		height: auto !important;
		width: 100% !important;
		position: absolute;
		top: 0px;
		right: 0px;
		bottom: 0px;
		left: 0px;
		margin: auto;
	}

	#detail_box {
		background: rgba(0, 0, 0, .9) !important;
		font-size: 16pt !important;
		font-stretch: condensed !important;
		opacity: 0;
		transition: all .3s ease-in-out !important;
	}

	.id3:hover #detail_box {
		opacity: 1 !important;
		transition: all .3s ease-in-out !important;
	}

